import {FormEvent,ChangeEvent,createContext,useState,useContext} from "react"
type contextType={
    name: string;
  email: string;
  gender: string;
  phno:string;
  address:string;
  dob :string;
  password:string;
  confirmpassword:string;
  inputOnChange?(event:ChangeEvent<HTMLInputElement>):void;
  userFormSubmit?(event:FormEvent):void;
}
type Props={
    children:JSX.Element;
}
let UserContext=createContext<contextType>({
    name: "",
        email: "",
        gender: "",
        phno:"",
        address:"",
        dob: "",
        password:"",
        confirmpassword:"",


})
export const UserContextProvider=(props:Props)=>{
    let {children}=props;
    let [user, setUser] = useState<contextType>({
        name: "",
        email: "",
        gender: "",
        phno:"",
        address:"",
        dob: "",
        password:"",
        confirmpassword:"",
    });
    const [userarray,setUserarray]=useState<contextType[]>([])
    let inputOnChange = (event: ChangeEvent<HTMLInputElement>) => {
        let { name, value } = event.currentTarget;
        setUser({ ...user, [name]: value });
        //console.log(value)
      };
      let userFormSubmit = (event: FormEvent) => {
        event.preventDefault();
        userarray.push(user);
        setUser({
            name: "",
            email: "",
            gender: "",
            phno:"",
            address:"",
            dob: "",
            password:"",
            confirmpassword:"",
        });
        setUserarray(userarray);


      };
      //console.log(userarray);

      let value:contextType={
        name: user.name,
        email: user.email,
        gender: user.gender,
        phno:user.phno,
        address:user.address,
        dob: user.dob,
        password:user.password,
        confirmpassword:user.confirmpassword,
        inputOnChange:inputOnChange,
        userFormSubmit:userFormSubmit,


    };
    return (
        <UserContext.Provider value={value}>{children}</UserContext.Provider>

    );
};
export const useUserContext=()=>{
    return useContext(UserContext);
}
