import {ChangeEvent,createContext,useState,useContext} from "react"
type contextType={
    name: string;
  email: string;
  gender: string;
  phno:string;
  address:string;
  dob :string;
  password:string;
  confirmpassword:string;
  inputOnChange?(event:ChangeEvent<HTMLInputElement>):void;
}
type Props={
    children:JSX.Element;
}
let UserContext=createContext<contextType>({
        name: "",
        email: "",
        gender: "",
        phno:"",
        address:"",
        dob: "",
        password:"",
        confirmpassword:"",

})
export const UserContextProvider=(props:Props)=>{
    let {children}=props;
    let [user, setUser] = useState<contextType>({
        name: "",
        email: "",
        gender: "",
        phno:"",
        address:"",
        dob: "",
        password:"",
        confirmpassword:"",
    });
    let inputOnChange = (event: ChangeEvent<HTMLInputElement>) => {
        let { name, value } = event.target;
        setUser({ ...user, [name]: value });
      };

      let value:contextType={
        name: user.name,
        email: user.email,
        gender: user.gender,
        phno:user.phno,
        address:user.address,
        dob: user.dob,
        password:user.password,
        confirmpassword:user.confirmpassword,
        inputOnChange,


    };
    return (
        <UserContext.Provider value={value}>{children}</UserContext.Provider>

    );
};
export const useUserContext=()=>{
    return useContext(UserContext);
}
